// refetch the data when the page arg changes\nexport async function load({\n\tparams,\n\tfetch\n}: any) {\n\tconst page = params?.slug ? params.slug : "ison";\n\tconst data = await fetch(`https://ison.gr/graphql`, {\n\t\tmethod: "POST",\n\t\theaders: {\n\t\t\t"Content-Type": "application/json"\n\t\t},\n\t\tbody: JSON.stringify({\n\t\t\tquery: `\n\t\t\t\t{\n\t\t\t\t\tpage(id: "${page}", idType: URI) {\n\t\t\t\t\t\ttitle\n\t\t\t\t\t\tcontent\n\t\t\t\t\t\tfeaturedImage {\n\t\t\t\t\t\t\tnode {\n\t\t\t\t\t\t\t\tsourceUrl\n\t\t\t\t\t\t\t}\n\t\t\t\t\t\t}\n\t\t\t\t\t}\n\t\t\t\t}\n\t\t\t`\n\t\t})\n\t});\n\tconst response = await data.json();\n\treturn {\n\t\tprops: {\n\t\t\tpage: response.data.page\n\t\t}\n\t};\n}